$(function() {
	/* 2-1. block ******************************************************************************************************* */
	$(window).on('resize', function(e) {
		if(Number($('section#block').attr('data-browser-size')) != getBrowserSize()) {
			$('.preloading').show();
			$('section#block *').removeAttr('style');

			clearTimeout(window.resizedFinished);
			window.resizedFinished = setTimeout(function(){
				location.reload();
			}, 0);
		}
	});


	var scrollEvent = false,
		count = 0;

	$(window).on('scroll', function(e) {
		nowScrollTop = $(this).scrollTop();
		var wt = $(document).scrollTop() + $(window).outerHeight();

		/* Btn goto-top ***************************************************************************************************** */
		if(nowScrollTop > 200) {
			if($('#goto-top').css('display') == 'none') $('#goto-top').fadeIn(200);
		} else {
			if($('#goto-top').css('display') == 'block') $('#goto-top').fadeOut(200);
		}

		/* 2-2. block ******************************************************************************************************* */
		if(!$('section#block').hasClass('_off')) movingBlock($(window).scrollTop());

		var t_top = $('section#template').offset().top,
			checkFixedBlockText = (wt > t_top) ? false : true;
		if(checkFixedBlockText) $('section#block:not(._off) .fixed-wrap').addClass('_fixed');
		else $('section#block:not(._off) .fixed-wrap').removeClass('_fixed');



		/* 2-2. template **************************************************************************************************** */
		if($(window).outerWidth() > 767) {
			var ts_top = $('section#template .template-site').offset().top,
				ts_bottom = $('section#review').offset().top - Number($('section#template').css('padding-bottom').replace('px','')) - $('header.land-nav').outerHeight(),
				checkShowTemplateSite = (wt > ts_top && $(document).scrollTop() < ts_bottom) ? true : false;

			if(checkShowTemplateSite) {
				var move_unit = $(document).outerWidth() / 1000;
				if(wheelDelta() == 'down') {
					$('section#template .template-site .template-wrap.left').css({'left':'-='+move_unit});
					$('section#template .template-site .template-wrap.right').css({'left':'+='+move_unit});
				} else if(wheelDelta() == 'up') {
					$('section#template .template-site .template-wrap.left').css({'left':'+='+move_unit});
					$('section#template .template-site .template-wrap.right').css({'left':'-='+move_unit});
				}
			} else {
				if(wt < ts_top) {
					$('section#template .template-site .template-wrap.left').css({'left':'-29vw'});
					$('section#template .template-site .template-wrap.right').css({'left':'-20vw'});
				}
				if(wt > ts_top) {
					$('section#template .template-site .template-wrap.left').css({'left':'-35vw'});
					$('section#template .template-site .template-wrap.right').css({'left':'-16vw'});
				}
			}
		} else {
			$('section#template .template-site .template-wrap').removeAttr('style');
		}

		prevScrollTop = nowScrollTop;
	});


	$('.block-text .btn, .review-bottom .btn, .detail-bottom .btn, .last-text .btn').off('click').on('click', function() {
		location.href = '/land/check/s/'+page;
	});

	/* 2-5. plan ******************************************************************************************************** */
	$('section#plan .plan-item .btn').off('click').on('click', function(e) {
		var plan_type = $(this).attr('data-plan');
		location.href = '/land/check/p/'+page+'/'+plan_type;
	});

});
